<?phpif ( ! defined( 'ABSPATH' ) ) exit;$connect_api = wp_remote_get( WOAAPIURL );$api_response = [];if ( is_array( $connect_api ) ) {	$api_response = $connect_api['body'];	$api_response = json_decode( $api_response, true );}if( $_POST && $_POST["TYPE"] == "WOA_AYARLAR"){	if (!isset($_POST['WOA_AYARLARI']) || ! wp_verify_nonce( $_POST['WOA_AYARLARI'], 'WOA_AYARLARI' ) ) {		print 'Sorry, you are not authorized to access this page!';		exit;	}else{		$woa_phone = sanitize_text_field($_POST['woa_phone']);		update_option('woa_phone', $woa_phone);		$woa_apikey = sanitize_text_field($_POST['woa_apikey']);		update_option('woa_apikey', $woa_apikey);		echo'<div class="alert alert-success" role="alert">Your Settings Have Been Updated!</div>';	}}$woa_phone     = get_option("woa_phone");$woa_apikey	= get_option("woa_apikey");$Burl = "https://wapiwp.com/api/orderpush/?apikey=".$woa_apikey;$Credit = wp_remote_post( $Burl, array(	'method'      => 'POST',	'timeout'     => 45,	'redirection' => 5,	'httpversion' => '1.0',	'blocking'    => true,	'headers'     => array(),	'body'        => array(		'credit' => true 	),	'cookies'     => array()));if ( !is_wp_error( $Credit ) ) {	$Credit = json_decode($Credit["body"]);	if($Credit->status == "success"){		$NowCredit = $Credit->credit;   	}else{		$NowCredit = "0";   	}}if(empty($woa_apikey)){	echo '<div class="alert alert-danger" role="alert">Please make  settings!</div>';}?><div class="container-fluid mt-4 col-8 mx-auto">        <!--HEADER-->        <div class="row mb-4">                   <div class="col bg-dark shadow p-3">			                <h4 class="p-0 text-white"> <img class="img-fluid" style="width: 50px;" src="<?php echo plugins_url( '/assets/images/logo.png', __FILE__ ); ?>"> Real-time Order Notify</h4>            </div>        </div>        <!--BODY-->        <div class="row">            <div class="col-md-8">                <div class="accordion" id="WmsgMenuAccordion"> 